mapper.select:
select cbt.community_board_id, cbt.user_id, ut.user_username, cbt.community_board_title, cbt.community_board_content, cbt.community_board_animal_category_id, cbact.community_board_animal_category_name, cbact.community_board_animal_category_name_kor, cbt.create_date, cbt.update_date from community_board_tb cbt left outer join user_tb ut on (ut.user_id = cbt.user_id) left outer join community_board_animal_category_tb cbact on (cbact.community_board_animal_category_id = cbt.community_board_animal_category_id) where 1 = 1
AND cbt.community_board_content LIKE CONCAT('%', #{searchText}, '%')
LIMIT #{startIndex}, #{count}

select cbt.community_board_id, cbt.user_id, ut.user_username, cbt.community_board_title, cbt.community_board_content, cbt.community_board_animal_category_id, cbact.community_board_animal_category_name, cbact.community_board_animal_category_name_kor, cbt.create_date, cbt.update_date
from community_board_tb cbt left outer join user_tb ut on (ut.user_id = cbt.user_id) left outer join community_board_animal_category_tb cbact on (cbact.community_board_animal_category_id = cbt.community_board_animal_category_id)
where cbt.community_board_animal_category_id = 2;

mapper.delete:
delete from community_board_tb
where community_board_id in (
#{boardId}
